
public class Days {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int day = 5;
		
		if (day ==0)
		{System.out.println("This is Sunday");}
		
		else
			if (day == 1)
			{System.out.println("This is Monday");}
		
			else
				if (day == 2)
				{System.out.println("This is Tuesday");}
		
	}

}
